import React from 'react';

// const CLIENT_ID = process.env.REACT_APP_SPOTIFY_CLIENT_ID;
// const REDIRECT_URI = process.env.REDIRECT_URI;
// const AUTH_ENDPOINT = process.env.AUTH_ENDPOINT;
// const RESPONSE_TYPE = 'token';

const CLIENT_ID = 'c51e5235da0849e3a6b4e04c0b035f2b';
const REDIRECT_URI = 'http://10.196.0.110:3000/redirect';
const AUTH_ENDPOINT = 'https://accounts.spotify.com/authorize';
const RESPONSE_TYPE = 'token';

console.log("CLIENT_ID:", CLIENT_ID); 
console.log("REDIRECT_URI:", REDIRECT_URI); 

function Login() {
  const handleLogin = () => {
    if (!CLIENT_ID || !REDIRECT_URI) {
      console.error("Spotify Client ID or Redirect URI is not set.");
      return;
    }
    // Redirecting user to Spotify's authorization page
    window.location.href = `${AUTH_ENDPOINT}?client_id=${CLIENT_ID}&redirect_uri=${REDIRECT_URI}&response_type=${RESPONSE_TYPE}&scope=user-read-private user-read-email`;
  };

  return (
    <div className="login">
      <h2>Welcome to Groove</h2>
      <p>Login to start using your Spotify account.</p>
      <button onClick={handleLogin}>Login with Spotify</button>
    </div>
  );
}

export default Login;
